        },{
            "name": "Requestor Email",
            "value": "["+requestorEmail+"]("+requestorEmailUrl+")"
        }],
    }],
    "potentialAction": [
      {
  			"@type": "OpenUri",
  			"name": "View Incident",
  			"targets": [
  				{
  					"os": "default",
  					"uri": data.env.JIRA_URL+'/browse/'+data.createIncidentResponse.body.key
  				}
  			]
  		}
    ]
   };

  return {url,body};"

"let userId = data.request.body.payload.metadata.audit.createUserId;
