"let productName=data.request.body.payload.spec.product;
  let productUrl=data.env.API_CENTRAL_URL+'/products/details/'+data.request.body.payload.spec.product;
  let subscriptionName=data.request.body.payload.title;
  let subscriptionUrl=data.env.API_CENTRAL_URL+'/marketplace/subscriptions';
  let requestorName=data.getUserResponse.body.result.firstname+' '+data.getUserResponse.body.result.lastname;
  let requestorEmail=data.getUserResponse.body.result.email;
  let requestorEmailUrl='mailTo:'+data.getUserResponse.body.result.email;
  let selfLink = data.env.API_CENTRAL_URL+'/apis'+data.request.body.payload.metadata.selfLink+'/approval';

  let dataToencode = data.env.JIRA_USERNAME+':'+data.env.JIRA_API_TOKEN;
  let buff = new Buffer(dataToencode);
  let encodedData = buff.toString('base64');

  let url = data.env.JIRA_URL+'/rest/api/2/issue';

  let headers = {
    Authorization : 'Basic '+encodedData,
    'Content-Type': 'application/json'
  };

  // Jira custom fields
  let self_link = data.env.JIRA_SUBS_SELF_LINK_FIELD;
  let subs_name = data.env.JIRA_SUBS_NAME_FIELD;

  let description = 'A marketplace product subscription request has been made. Details are below:'+'\r'+'\r';
  description += 'Subscription: '+subscriptionName+'\r';
  description += 'Subscription Link: '+subscriptionUrl+'\r';
  description += 'Product: '+productName+'\r';
  description += 'Product Link: '+productUrl+'\r';
  description += 'Requestor: '+requestorName+'\r';
  description += 'Requestor E-mail: '+requestorEmail+'\r';
  description += '\r';
  description += "In order to approve or reject the subscription request, set the Approve/Reject field, add a comment/reason and close the incident.\r\r";
  description += "You can review the susbscription request here:\n"+subscriptionUrl+"\n\n";

  let body = {
    "fields": {
      "summary": "Marketplace product subscription approval request: "+subscriptionName,
      "issuetype": {
        "name": "Demand"
      },
      "project": {
        "key": data.env.JIRA_PROJECT_KEY
      },
      "description": description
    }
  };

  body.fields[self_link] = selfLink;
  body.fields[subs_name] = subscriptionName;

  return {url, headers, body};"
