"// customfield_10050 = Jira custom field 'Subscription Self Link'
  // customfield_10052 = Jira custom field 'Subscription Name'

  let isProject = data.request.body.issue.fields.project.key === data.env.JIRA_PROJECT_KEY;
  let isDone = data.request.body.issue.fields.status.name === 'Done';

  //https://stackoverflow.com/questions/5515310/is-there-a-standard-function-to-check-for-null-undefined-or-blank-variables-in
  let hasSelfLink = false;
  if (data.request.body.issue.fields.customfield_10050) {hasSelfLink = true;}

  let hasApprovalSatatus = false;
  if (data.request.body.issue.fields.customfield_10049) {hasApprovalSatatus = true;}


  return isProject && isDone && hasSelfLink && hasApprovalSatatus;"

"let dataToencode = data.env.CLIENT_ID+':'+data.env.CLIENT_SECRET;
  let buff = new Buffer(dataToencode);
  let encodedData = buff.toString('base64');

  let url = 'https://login.axway.com/auth/realms/Broker/protocol/openid-connect/token';
  let headers = {
    Authorization : 'Basic '+encodedData,
   'Content-Type': 'application/x-www-form-urlencoded'
  };
  let body = {
    grant_type: 'client_credentials'
  };

 return {url,headers,body};"

"let url = data.request.body.issue.fields.customfield_10050;
   let headers = {
     'Content-Type': 'application/json',
     Authorization: 'Bearer '+data.getTokenResponse.body.access_token
   };

  	let approvalStatus = 'rejected';

  	if(data.request.body.issue.fields.customfield_10049.value === 'Approve') {
       approvalStatus = 'approved';
    }

   let body = {
      "approval": {
      "state": approvalStatus,
      "reason": data.request.body.issue.fields.customfield_10051
      }
   };
   return {url,headers,body};"

"let url=data.env.MS_TEAMS_WEBHOOK_URL;

  let body = {
    text: 'Subscription "'+data.request.body.issue.fields.customfield_10052+'" set to '+data.request.body.issue.fields.customfield_10049.value+' by '+data.request.body.user.displayName+' in Jira'
  };

