"let dataToencode = data.env.JIRA_USERNAME+':'+data.env.JIRA_API_TOKEN;
  let buff = new Buffer(dataToencode);
  let encodedData = buff.toString('base64');

  let url = data.env.JIRA_URL+'/rest/api/2/issue/'+data.request.body.issue.key+'/comment';

  let headers = {
    Authorization : 'Basic '+encodedData,
    'Content-Type': 'application/json'
  };

  let body = {
    body: 'Subscription "'+data.request.body.issue.fields.customfield_10052+'" set to '+data.request.body.issue.fields.customfield_10049.value+' by '+data.request.body.user.displayName+' in Jira and the subscription approval workflow'
  };

  return {url, body, headers};"
